core.microhandlers.add('dashboard-tabs','.js-tabs.dashboard-tabs .tabs li a',function(){var link=jQuery(this),li=link.parent();link.click(function(event){if(!li.hasClass('tab-current')){var id=link.data('id');li.parent().find('li.tab-current').removeClass('tab-current');li.addClass('tab-current');var box=li.parents('.js-tabs.dashboard-tabs').eq(0);box.find('.tab-content').hide();box.find('#'+id).show()};return false})});core.microhandlers.add('dashboard-welcome','.admin-welcome',function(){$('.dashboard').addClass('welcome-visible')})