CommonForm.elementControllers.push({pattern:'.line .input-field-wrapper.switcher:not(.switcher-read-only)',handler:function(){var input=jQuery(':checkbox',this),cnt=jQuery(this),widget=jQuery('.widget',this);widget.click(function(){if(!input.prop('disabled')){input.click();input.change()}});input.change(function(){if(this.checked){cnt.addClass('enabled').removeClass('disabled');widget.attr('title',widget.data('enabled-label'))}else{cnt.removeClass('enabled').addClass('disabled');widget.attr('title',widget.data('disabled-label'))}})}})