core.microhandlers.add('login-timer','.login-box',function(){var timeLeft=jQuery(this).data('time-left');if(timeLeft)(function(){timeLeft--;if(0<timeLeft){var min=parseInt(timeLeft/60),sec=timeLeft%60;jQuery('#timer').text((10>min?'0':'')+min+':'+(10>sec?'0':'')+sec);setTimeout(arguments.callee,1e3)}else jQuery('.login-box').removeClass('locked')})()});core.microhandlers.add('input','[name="login"]',function(event){var input=this;jQuery(function(){input.focus()})});core.microhandlers.add('login-forgot-password','.login-box',function(){var box=jQuery(this),inp=box.find('input[name="login"]').eq(0);box.find('.forgot-password a').click(function(event){if(inp.val()){var link=jQuery(event.currentTarget),url=link.attr('href');url+=(url.search(/\?/)===-1?'?':'&')+'email='+encodeURIComponent(inp.val());link.attr('href',url)};return true})})