var WelcomeBlock=Object.extend({constructor:function WelcomeBlock(base){this.base=base;this.closeButton=$('.close-button',base);this.foreverCheckbox=$('.hide-welcome-block',base);this.assignCloseHandler()},assignCloseHandler:function(){var self=this,action=this.base.data('close-action'),target=this.base.data('close-target');this.closeButton.click(function(){var data={forever:self.foreverCheckbox.is(':checked')?'1':'0',block:self.base.data('block-name')};core.post({target:target,action:action},null,data);self.base.hide();if(!jQuery('.admin-welcome:visible').length)jQuery('.dashboard.welcome-visible').removeClass('welcome-visible')})}});core.autoload(WelcomeBlock,'.admin-welcome')