function CodeMirrorWidget(base){var element=base,mode=element.data('codemirrorMode'),width=element.outerWidth(),height=element.outerHeight(),editor=CodeMirror.fromTextArea(element.get(0),{mode:mode,lineNumbers:true,viewportMargin:Infinity});$('.CodeMirror').resizable({resize:function(){editor.setSize($(this).width(),$(this).height())}});editor.setSize(width,height);jQuery(element).data('CodeMirror',editor);editor.on('change',function(editor){element.text(editor.doc.getValue()).val(editor.doc.getValue()).trigger('change')})};core.autoload(CodeMirrorWidget,'textarea.codemirror.autoloadable')